mapper.insert:
INSERT INTO attach (
    atch_no
    , atch_parent_no
    , atch_file_name
    , atch_original_name
    , atch_file_size
    , atch_fancy_size
    , atch_content_type
    , atch_path
    , atch_down_hit
    , atch_del_yn
    , atch_reg_date
) VALUES (
    seq_attach_no.NEXTVAL
    , #{atchParentNo}
    , #{atchFileName}
    , #{atchOriginalName}
    , #{atchFileSize}
    , #{atchFancySize}
    , #{atchContentType}
    , #{atchPath}
    , 0
    , 'N'
    , SYSDATE
)

mapper.update:
UPDATE attach
   SET atch_down_hit = atch_down_hit + 1
 WHERE atch_no = #{atchNo}
